fn day1(text: String) -> isize {
    // let up = text.chars().filter(|&c| c == '(').count();
    // let down = text.chars().filter(|&c| c == ')').count();
    let mut count = 0;
    let mut step = 0;
    for char in text.chars() {
        step += 1;
        if char == '(' {
            count += 1
        }
        if char == ')' {
            count -= 1
        }
        if count == -1 {
            return step;
        }
    }
    count
}

